<?php

/**
 * EmpresaSectorDosTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class EmpresaSectorDosTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object EmpresaSectorDosTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('EmpresaSectorDos');
    }

    /**
     * Devuelve el nombre legible del slug
     * @static
     * @param $slug
     */
    public static function getName($slug)
    {
        $q = self::getInstance()->createQuery('q')
            ->select('q.name')
            ->where('q.slug = ?', $slug);
        return $q->fetchOne();
    }

    /**
     * Comprueba si el sector dos tiene actividad asociada.
     * @static
     * @param $sector_dos_id
     */
    public static function hasActividad($sector_dos_id)
    {
        $q = Doctrine_Core::getTable('EmpresaSectorTres')->createQuery('q')
           ->where('q.empresa_sector_dos_id = ?', $sector_dos_id);

        return $q->count() > 0 ? true : false;
    }
    
    public function getSectoresDosOrderByOrden()
    {
        $elements  = $this->createQuery()->orderBy('orden')->execute();
    
        return $elements;
    }
}