<?php

/**
 * GiftRedemptionTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class GiftRedemptionTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object GiftRedemptionTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('GiftRedemption');
    }
    
    /**
     * fetch redeem gift query
     * @param String $user_id
     * @return Doctrine_Query
     */
    public function getRedeemGiftRecords($user_id){
      // create gift redeem query
        $gift_redeem_query = Doctrine_Query::create()
                                                 ->from('GiftRedemption  gr')
                                                 ->where('gr.user =?', $user_id)
                                                 ->orderBy('gr.created_at desc');
        return $gift_redeem_query;
    }
}