class KpiEmpresaProducto extends BaseKpiEmpresaProducto
{

    /**
     * AÃ±ade el valor y recalcula la media...
     *
     * @param $value
     */
    public function addValue($value)
    {
        $this->setNumeroValores($this->getNumeroValores() + 1);
        $this->setSumaValores($this->getSumaValores() + $value);

        $this->save();
    }

    public function getMedia()
    {
        if($this->getNumeroValores() == 0) {
            return 0;
        }
        $media = $this->getSumaValores() / $this->getNumeroValores();
        return round($media, 2);
    }

    /**
     * @param $value
     */
    public function substractValue($value)
    {
        $this->setNumeroValores($this->getNumeroValores() - 1);
        $this->setSumaValores($this->getSumaValores() - $value);

        $this->save();
    }
}
