<?php

/**
 * ListaCuestionarioRespuestaTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ListaCuestionarioRespuestaTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object ListaCuestionarioRespuestaTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('ListaCuestionarioRespuesta');
    }

    /**
     * fetch reply value 
     * @param String $list_question_user_id List Question User Id
     * @param String $list_question_id List Question Id
     * @return String
     */
    public function getRecordByListQuestionUserId($list_question_user_id, $list_question_id){
      //create query
      $question_query = Doctrine_Query::create()  
                                        ->from('ListaCuestionarioRespuesta lcr')
                                        ->where('lcr.lista_cuestionario_pregunta_id =?', $list_question_id)
                                        ->andWhere('lcr.lista_cuestionario_user_id =?', $list_question_user_id);
      $question_record = $question_query->fetchOne();
      return $question_record ? $question_record['respuesta'] : null;
    }
}