<?php

/**
 * ProductFavouriteListTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ProductFavouriteListTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object ProductFavouriteListTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('ProductFavouriteList');
    }
    
    /**
     * fetch favourit product record
     * @param String $user_id User Id
     * @param String $compnay_id Company Id
     * @return Record
     */
    public function getRecordByUserAndId($user_id, $product_id){
      //create favourit query
      $favourit_query = Doctrine_Query::create()
                        ->from('ProductFavouriteList pfl')
                        ->where('pfl.user_id =?', $user_id)
                        ->andWhere('pfl.product_id =?', $product_id);
      //return favourit record
      return $favourit_query->fetchOne();
                
    }
}