<?php

/**
 * ProductoTipoDosTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ProductoTipoDosTable extends Doctrine_Table {

    /**
     * Returns an instance of this class.
     *
     * @return object ProductoTipoDosTable
     */
    public static function getInstance() {
        return Doctrine_Core::getTable('ProductoTipoDos');
    }

    /**
     * Comprueba si el sector tiene actividades asociadas.
     * @static
     * @param $sector_id
     * @return bool
     */
    public static function hasActividad($sector_id) {
        $q = Doctrine_Core::getTable('ProductoTipoTres')->createQuery('q')
                ->where('q.producto_tipo_dos_id = ?', $sector_id);

        return $q->count() > 0 ? true : false;
    }

    /**
     * Devuelve el nombre legible del
     * @static
     * @param $slug
     */
    public static function getName($slug) {
        $q = self::getInstance()->createQuery('q')
                ->select('q.name')
                ->where('q.slug = ?', $slug);
        return $q->fetchOne();
    }

}