<?php

/**
 * ProfesionalEstadoTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ProfesionalEstadoTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object ProfesionalEstadoTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('ProfesionalEstado');
    }

    public function getEstedioName()
    {
        return Doctrine_Query::create()
                ->select('name')
                ->from('ProfesionalEstado')
                ->Where('name = ?', 'Selecciona estado')
                ->orWhere('name = ?','Revista')
                ->orWhere('name = ?','Activo')
                ->orWhere('name = ?', 'Borrador')
                ->execute();
    }
}