class RecomendedRegistration extends BaseRecomendedRegistration
{

  /**
   * create new records for recomended user and registered user
   * @param String $recomended_user Recomended user 
   * @param String $registered_user Register User 
   * @param String $email Email Address
   */
  public function create($recomended_user, $registered_user,$email){
    //set attributes
    $this->setRegisteredUser($registered_user);
    $this->setRecomendedUser($recomended_user);
    $this->setEmail($email);
    $this->setCreatedAt(date('Y-m-d H:i:s'));
    //save records
    $this->save();
  }
}